import { r as resolve } from '../shared/nuxi.28ceb2e0.mjs';
import { c as consola } from '../shared/nuxi.5520c85f.mjs';
import { w as writeTypes } from '../shared/nuxi.7a1c1d3e.mjs';
import { l as loadKit } from '../shared/nuxi.15ba7947.mjs';
import { c as clearDir } from '../shared/nuxi.238c4151.mjs';
import { o as overrideEnv } from '../shared/nuxi.6840794e.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.99740dab.mjs';
import '../shared/nuxi.ccadf1fb.mjs';
import 'util';
import 'path';
import 'fs';
import 'os';
import 'tty';
import 'node:fs';
import '../shared/nuxi.2f82a09f.mjs';
import 'node:module';
import 'node:url';

const buildCommand = defineNuxtCommand({
  meta: {
    name: "build",
    usage: "npx nuxi build [--prerender] [rootDir]",
    description: "Build nuxt for production deployment"
  },
  async invoke(args) {
    overrideEnv("production");
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt, buildNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({
      rootDir,
      overrides: {
        _generate: args.prerender
      }
    });
    await clearDir(nuxt.options.buildDir);
    await writeTypes(nuxt);
    nuxt.hook("build:error", (err) => {
      consola.error("Nuxt Build Error:", err);
      process.exit(1);
    });
    await buildNuxt(nuxt);
  }
});

export { buildCommand as default };
