import { e as execa } from '../shared/nuxi.759d9bcf.mjs';
import { r as resolve } from '../shared/nuxi.28ceb2e0.mjs';
import { t as tryResolveModule } from '../shared/nuxi.2f82a09f.mjs';
import { l as loadKit } from '../shared/nuxi.15ba7947.mjs';
import { w as writeTypes } from '../shared/nuxi.7a1c1d3e.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.99740dab.mjs';
import 'node:buffer';
import 'node:path';
import 'node:child_process';
import 'node:process';
import 'child_process';
import 'path';
import '../shared/nuxi.ccadf1fb.mjs';
import 'fs';
import 'node:url';
import 'os';
import 'node:os';
import 'assert';
import 'events';
import 'buffer';
import 'stream';
import 'util';
import 'node:module';
import 'node:fs';

const typecheck = defineNuxtCommand({
  meta: {
    name: "typecheck",
    usage: "npx nuxi typecheck [rootDir]",
    description: "Runs `vue-tsc` to check types throughout your app."
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({ rootDir, config: { _prepare: true } });
    await writeTypes(nuxt);
    await nuxt.close();
    const hasLocalInstall = tryResolveModule("typescript", rootDir) && tryResolveModule("vue-tsc/package.json", rootDir);
    if (hasLocalInstall) {
      await execa("vue-tsc", ["--noEmit"], { preferLocal: true, stdio: "inherit", cwd: rootDir });
    } else {
      await execa("npx", "-p vue-tsc -p typescript vue-tsc --noEmit".split(" "), { stdio: "inherit", cwd: rootDir });
    }
  }
});

export { typecheck as default };
